INSERT INTO couch_travelling.museum (id, name, description, website, idCountry, video, lati, cam1, cam2, cam3, image, longi) VALUES (1, 'Le musée d’Orsay', 'Le musée d’Orsay est un musée national inauguré en 1986, situé dans le 7e arrondissement de Paris le long de la rive gauche de la Seine. Il est installé dans l’ancienne gare d''Orsay, construite par Victor Laloux de 1898 à 1900 et réaménagée en musée sur décision du Président de la République Valéry Giscard d''Estaing.', 'https://www.musee-orsay.fr/', 1, 'https://www.youtube.com/embed/PArCzayWAjg', '48.860175', '1511477795', '1581969002', '1228218512', 'musee-dorsay.jpg', '2.326615');
INSERT INTO couch_travelling.museum (id, name, description, website, idCountry, video, lati, cam1, cam2, cam3, image, longi) VALUES (2, 'Le musée américain d''histoire naturelle', 'Le musée américain d''histoire naturelle (dont le nom officiel en anglais est American Museum of Natural History) est le principal musée d''histoire naturelle de la ville de New York, aux États-Unis, et aussi l''un des plus grands musées de la ville. Il est situé sur l''île de Manhattan dans le quartier de l''Upper West Side, au croisement de la 81e rue et Central Park West, face à Central Park.', 'https://www.amnh.org/', 2, 'https://www.youtube.com/embed/5QRPyNDZ8MA', '40.780747', '1483960655', '1352126160', '1483960550', 'American-Museum.jpg', '-73.972636');
INSERT INTO couch_travelling.museum (id, name, description, website, idCountry, video, lati, cam1, cam2, cam3, image, longi) VALUES (3, 'Le musée national de Rio', 'Le musée national de l''université fédérale de Rio de Janeiro (en portugais : Museu Nacional da Universidade Federal do Rio de Janeiro1) ou juste musée national, anciennement musée national de Rio de Janeiro, parfois nommé musée d''histoire naturelle.', 'http://www.museunacional.ufrj.br/', 3, 'https://www.youtube.com/embed/CPmtqHLFEVc', '-23.561640', '1580415336', '1575123268', '1249392511', '8729c82cde4d399727259455864255b6.jpg', '-46.656257');
INSERT INTO couch_travelling.museum (id, name, description, website, idCountry, video, lati, cam1, cam2, cam3, image, longi) VALUES (4, 'Le Museum of Contemporary Art', 'Le Museum of Contemporary Art, aussi connu sous l''acronyme MCA, est un musée australien situé à Sydney, qui se consacre exclusivement à l''exposition et la collection d''art contemporain, à la fois de toute l''Australie et du monde entier.', 'https://www.mca.com.au/', 4, 'https://www.youtube.com/embed/UuW-81cb8Ko', '-33.859945', '1503353468', '1179853135', '1380813015', 'MCA_Architecture-116_Pieter_Naessens.jpg', '151.208740');
INSERT INTO couch_travelling.museum (id, name, description, website, idCountry, video, lati, cam1, cam2, cam3, image, longi) VALUES (5, 'Le Musée russe d''État', 'Le Musée russe d''État, anciennement le Musée russe de Sa Majesté impériale Alexandre III, situé sur la place des Arts à Saint-Pétersbourg , est le plus grand dépositaire d'' art russe au monde. C''est également l''un des plus grands musées du pays avec une superficie totale de 30 hectares.', 'http://en.rusmuseum.ru/', 5, 'https://www.youtube.com/embed/49YeFsx1rIw', '59.937818', '1495633357', '1268401697', '1311081537', 'state-russian-museum-in-st-petersburg.jpg', '30.331910');
INSERT INTO couch_travelling.museum (id, name, description, website, idCountry, video, lati, cam1, cam2, cam3, image, longi) VALUES (6, 'Le musée de Shanghai', 'Le musée de Shanghai est un musée d''art chinois ancien situé à Puxi sur la Place du Peuple dans le district de Huangpu à Shanghai, Chine. L''entrée y est gratuite. Il est situé en face de la Mairie de Shanghai.', 'http://www.shanghaimuseum.net', 6, 'https://www.youtube.com/embed/HSH5SVVx3wA', '31.227344', '1567484660', '1585488387', '1585488739', 'Shanghai.jpg', '121.477627');
INSERT INTO couch_travelling.museum (id, name, description, website, idCountry, video, lati, cam1, cam2, cam3, image, longi) VALUES (7, 'Le musée Te Papa Tongarewa', 'Le musée de la Nouvelle-Zélande Te Papa Tongarewa est le musée national de la Nouvelle-Zélande situé à Wellington.
INSERT INTO couch_travelling.museum (id, name, description, website, idCountry, video, lati, cam1, cam2, cam3, image, longi) VALUES (8, 'Le musée de l''Acropole d''Athènes', 'Le musée de l''Acropole d''Athènes est un des principaux musées archéologiques d''Athènes en Grèce. Il dépend du ministère grec de la Culture (premier Éphorat des antiquités préhistoriques et classiques). Le président de son conseil d''administration est Dimitrios Pandermalis.', 'https://www.namuseum.gr/en/', 8, 'https://www.youtube.com/embed/g-rdv6xdUeE', '37.967663', '1514869904', '1587484462', '1448710542', 'athens-museums-1280.jpg', '23.728944');
INSERT INTO couch_travelling.museum (id, name, description, website, idCountry, video, lati, cam1, cam2, cam3, image, longi) VALUES (9, 'Le musée égyptien du Caire', 'Le musée égyptien du Caire est l’un des plus grands musées entièrement consacré à l’Antiquité égyptienne. Il a reçu plus de cent millions de visiteurs au xxe siècle.', 'http://gem.gov.eg/', 9, 'https://www.youtube.com/embed/EdCReWs6-wI', '30.043708', '1220422386', '1576525886', '1491387551', 'Egyptian-Museum-768x430.jpg', '31.224274');
INSERT INTO couch_travelling.museum (id, name, description, website, idCountry, video, lati, cam1, cam2, cam3, image, longi) VALUES (10, 'Le Musée de l''apartheid', 'Le Musée de l''apartheid, situé à Johannesburg en Afrique du Sud, est un complexe muséal consacré à l''histoire de l''Afrique du Sud au xxe siècle et plus spécialement axé sur l''apartheid, depuis ses débuts en 1948, son développement, et son abolition en 1994.', 'https://www.apartheidmuseum.org/', 10, 'https://www.youtube.com/embed/nzizOshJxmM', '-26.237765', '1523408955', '1499155860', '1523408955', 'tour-apartheid-museum.jpg', '28.007588');
INSERT INTO couch_travelling.museum (id, name, description, website, idCountry, video, lati, cam1, cam2, cam3, image, longi) VALUES (11, 'Le musée d''Edo-Tokyo', 'Le musée d''Edo-Tokyo (Edo Tokyo Hakubutsukan?) établi en 1993 est consacré à l''histoire de la ville de Tokyo. Les principaux éléments des expositions permanentes sont la réplique grandeur nature du Nihonbashi, pont qui servait traditionnellement de point de départ de toutes les routes du Japon en sortant d''Edo ; le théâtre Nakamuraza ; des modèles réduits de la ville et de bâtiments des ères Edo, Meiji et Showa.', 'https://www.edo-tokyo-museum.or.jp/en/', 11, 'https://www.youtube.com/embed/PLIKll5OjPQ', '35.696893', '1525295653', '1586320341', '1345863028', '3070_01.jpg', '139.797354');
INSERT INTO couch_travelling.museum (id, name, description, website, idCountry, video, lati, cam1, cam2, cam3, image, longi) VALUES (12, 'Le Musée national de New Delhi', 'Le Musée national (en anglais : National Museum) est l''un des plus grands musées d''Inde. Il a été créé en 1949 et contient des œuvres et objets allant de la période préhistorique jusqu''à l''art moderne.', 'http://www.nationalmuseumindia.gov.in/', 12, 'https://www.youtube.com/embed/7aVM1LHkqJM', '28.622654', '1503699073', '1509611733', '1234953077', 'National-Museum-building-situated-in-New-Delhi,-India.jpg', '77.213880');